package ch.bbw.zork;public class Command {	private String commandWord;	private String secondWord;	public Command(String fw, String sw) {		commandWord = fw;		secondWord = sw;	}	public String getCommandWord() {		return commandWord;	}	public String getSecondWord() {		return secondWord;	}	public boolean isUnknown() {		return (commandWord == null);	}	public boolean hasSecondWord() {		return (secondWord != null);	}					}